mapper.select:
SELECT DEL_YN
  FROM LIKE_BOARD
 WHERE USER_ID = #{userId}
   AND BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}

mapper.insert:
INSERT INTO LIKE_BOARD
      (IDX
      ,USER_ID
      ,BOARD_IDX
      ,BOARD_TYPE
      ,DEL_YN
      )
VALUES
     ((SELECT NVL(MAX(idx), 0) + 1 FROM like_board ALIAS_FOR_SUBQUERY)
      ,#{userId}
      ,#{boardIdx}
      ,#{boardType}
      ,'N'
      )

mapper.update:
UPDATE LIKE_BOARD
   SET DEL_YN = 'N'
 WHERE USER_ID = #{userId}
   AND BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}

UPDATE LIKE_BOARD
   SET DEL_YN = 'Y'
 WHERE USER_ID = #{userId}
   AND BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}

UPDATE TIP_BOARD
   SET LIKE_COUNT = LIKE_COUNT+1
 WHERE NUM = #{num}

UPDATE TIP_BOARD
   SET LIKE_COUNT = LIKE_COUNT-1
 WHERE NUM = #{num}
