mapper.insert:
INSERT INTO TIP_BOARD
      (NUM
      ,CATEGORY
      ,WRITER
      ,SUBJECT
      ,CONTENT
      ,REG_DATE
      ,DEL_YN
      )
VALUES
      ((SELECT NVL(MAX(num), 0) + 1 FROM tip_board ALIAS_FOR_SUBQUERY)
      ,#{category}
      ,#{writer}
      ,#{subject}
      ,#{content}
      ,(SELECT date_format(now(),'%Y-%c-%d'))
      ,'N'
      )

mapper.update:
UPDATE TIP_BOARD
   SET DEL_YN = 'Y'
 WHERE NUM = #{num}

UPDATE TIP_BOARD
   SET CATEGORY = #{category}
      ,SUBJECT = #{subject}
      ,CONTENT = #{content}
      ,UPD_DATE = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE NUM = #{num}

mapper.select:
SELECT NUM
  FROM TIP_BOARD
 WHERE WRITER = #{writer}
   AND SUBJECT = #{subject}
   AND CONTENT = #{content}
   AND DEL_YN = 'N'
 ORDER BY NUM desc limit 1
